import React, { useEffect, useState } from "react";
import { Container, Row, Col, Table, Card, Badge } from "react-bootstrap";
import axios from "axios";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faServer,
  faGlobe,
  faPlug,
  faDesktop,
  faEthernet,
  faHdd,
  faLeaf,
} from "@fortawesome/free-solid-svg-icons";

function App() {
  const [total, setTotal] = useState(0);
  const [active, setActive] = useState(0);
  const [inActive, setInActive] = useState(0);
  const [instances, setInstances] = useState([]);

  const fetchData = () => {
    axios
      .get("http://localhost:8010/proxy", {
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      })
      .then((res) => {
        const data = res.data;
        const instancesData = data.applications.application;
        console.log(instancesData);
        let instancesArray = instances;
        if (instancesArray.length === 0) {
          instancesData.map((item) => {
            instancesArray.push({
              name: item.name,
              data: item.instance[0],
              status: "up",
            });
          });
          setInstances(instancesArray);
          setTotal(instancesArray.length);
        }
        console.log(instancesArray);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    fetchData();
  });

  return (
    <Container className="container">
      <Row>
        <Col className="mt-3 mb-1">
          <h2>
            <FontAwesomeIcon icon={faLeaf} className="mr-2" />
            Eureka dashboard
          </h2>
        </Col>
      </Row>
      <Row>
        <Col>
          <Card bg="primary" text="white">
            <Card.Body>
              <h4>
                <FontAwesomeIcon icon={faServer} className="mr-2" />
                Total
              </h4>
              <div>
                <h1 className="d-inline">{total}</h1>
                <h5 className="d-inline ml-2">node</h5>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col>
          <Card bg="success" text="white">
            <Card.Body>
              <h4>
                <FontAwesomeIcon icon={faGlobe} className="mr-2" />
                Active
              </h4>
              <div>
                <h1 className="d-inline">{active}</h1>
                <h5 className="d-inline ml-2">node</h5>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col>
          <Card bg="danger" text="white">
            <Card.Body>
              <h4>
                <FontAwesomeIcon icon={faPlug} className="mr-2" />
                Inactive
              </h4>
              <div>
                <h1 className="d-inline">{inActive}</h1>
                <h5 className="d-inline ml-2">node</h5>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col className="mt-3">
          <Table>
            <thead>
              <tr>
                <th>
                  <FontAwesomeIcon icon={faDesktop} className="mr-2" />
                  Application Name
                </th>
                <th>
                  <FontAwesomeIcon icon={faEthernet} className="mr-2" />
                  Host
                </th>
                <th>
                  <FontAwesomeIcon icon={faHdd} className="mr-2" />
                  Instance
                </th>
                <th>
                  <FontAwesomeIcon icon={faGlobe} className="mr-2" />
                  Status
                </th>
              </tr>
            </thead>
            <tbody>
              {instances.map((item) => {
                return (
                  <tr key={item.name}>
                    <td>{item.name}</td>
                    <td>{item.data.hostName}</td>
                    <td>{item.data.instanceId}</td>
                    <td>
                      <Badge variant="success">{item.status}</Badge>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
